* {
  box-sizing: border-box;
}

body {
  overflow: hidden;
  background: #000;
  color: #fff;
  font-family: "Playfair Display", cursive;
}

.b--hero-a {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100vh;
}
.b--hero-a__wrapper {
  overflow: hidden;
  cursor: grab;
  width: 100%;
  position: relative;
  z-index: 1;
  height: 40vh;
}
.b--hero-a__wrapper--is-dragging {
  cursor: grabbing;
}
.b--hero-a__wrapper__content {
  display: flex;
  position: absolute;
  z-index: 1;
  height: 100%;
  top: 0;
  left: 0;
  width: 100%;
}

.b--card-b {
  position: absolute;
  z-index: 1;
  top: 0;
  left: 0;
  width: 30vw;
  height: 100%;
  padding: 0 1.5vw;
  overflow: hidden;
}
.b--card-b:hover {
  opacity: 0.3;
}
.b--card-b__wrapper {
  position: relative;
  z-index: 1;
  display: block;
  user-select: none;
  padding: 0;
  border: none;
  outline: none;
  box-shadow: none;
  cursor: pointer;
  width: 100%;
  height: 100%;
  overflow: hidden;
  transform-origin: center;
}
.b--card-b__wrapper__link {
  display: block;
  height: 100%;
  position: relative;
  color: white;
}
.b--card-b__wrapper__link__bg-items {
  background-position: cover;
  height: 100%;
  z-index: 1;
}
.b--card-b__wrapper__link__ft-items {
  position: absolute;
  z-index: 10;
  bottom: 8px;
  left: 8px;
  right: 8px;
}
.b--card-b__wrapper__link__ft-items__title {
  margin: 0;
  padding: 0;
}

